mapper.select:
SELECT cic.*, ci.trip_id
FROM cooperative_item_checks cic
INNER JOIN cooperative_items ci ON cic.item_id = ci.id
WHERE ci.trip_id = #{tripId}
  AND cic.user_id = #{userId}

mapper.delete:
DELETE cic
FROM cooperative_item_checks cic
INNER JOIN cooperative_items ci ON cic.item_id = ci.id
WHERE ci.trip_id = #{tripId}
  AND cic.user_id = #{userId}
