mapper.select:
SELECT 
FROM trip_templates


        AND is_public = #{isPublic}


        AND type = #{type}


        AND destination LIKE CONCAT('%', #{destination}, '%')


ORDER BY created_at DESC

SELECT COUNT(*)
FROM trip_templates


        AND is_public = #{isPublic}


        AND type = #{type}


        AND destination LIKE CONCAT('%', #{destination}, '%')
